<template>    <div class="avatar" :title="fullName">        <img :src="userAvatarUrl" />        <span>{{ displayName }}</span>    </div></template><style scoped>    .avatar {        text-align: center;        width: 50px;    }    .avatar img,    .avatar span {        display: block;        margin: 0 auto;    }</style><script>  const md5 = require('md5')  import { estimationFrom } from '../helpers'  export default {    name: 'Avatar',    props: ['user'],    methods: {},    data () {      return {}    },    computed: {			displayName () {        return this.user ? this.user.displayName : '--'      },      fullName () {				return this.user ? this.user.firstname + ' ' + this.user.lastname : ''      },      userAvatarUrl () {        let url        if (!this.user || this.user.id === 0) {          url = '/static/avatar-unassigned.png'        } else {          url = 'https://www.gravatar.com/avatar/' + md5(String(this.user.mail).toLowerCase()) + '?s=50'        }        return url      }    }  }</script>